process.env.taf_stream   = "../../../../../taf-stream";
process.env.taf_rpc_path = "../../../../";

var TafSvr = require(process.env.taf_rpc_path).server;
var TRom   = require("./NodeJsCommImp.js").TRom;

TafSvr.getServant("./TRom.NodeJsTestServer.config.conf").forEach(function (config){
    var svr, map;
    map = {
        'TRom.NodeJsTestServer.NodeJsCommObj' : TRom.NodeJsCommImp
    };

    svr = TafSvr.createServer(map[config.servant]);
    svr.start(config);
});
