var TafSvr = require("../../../protal.js").server;
var taf   = require("./QueryF.js").taf;

function StartLocator(){
    var option = new TafSvr.BindAdapterOption();
    option.endpoint  = "tcp -h 127.0.0.1 -p 14005 -t 10000";
    option.name      = "locator";
    option.servant   = "Test.test.locatorObj";
    option.handleImp = taf.QueryFImp;
    var svr = TafSvr.createServer();
    svr.start(option);
    return svr;
}

module.exports = StartLocator