var Taf  = require("@tencent/taf-rpc");
var TafNode = require("./TafNodeTestServerImp.js").TafNode;
var svr = new Taf.server();

svr.initialize(process.env.TAF_CONFIG || "./local.conf", function (server){
    var servantName = server.Application + "." + server.ServerName + ".TafNodeTestObj";
    server.addServant(TafNode.TafNodeTestImp, servantName);
    console.log("server listening on ip:",server._adapters[0]._endpoint.sHost,",port:",server._adapters[0]._endpoint.iPort);
    console.log("server running on pid:",process.pid);
    svr.start();
});




