process.env.taf_rpc_path = "../../../../taf-rpc";
process.env.taf_stream   = "../../../../taf-stream";

var Taf  = require(process.env.taf_rpc_path).client;
var TRom = require("./NodeSecondProxy.js").TRom;

//生成代理类
var prx = Taf.stringToProxy(TRom.NodeSecondProxy, "TRom.NodeSecondServer.NodeSecondObj@tcp -h 127.0.0.1 -p 14003 -t 10000");

var success = function (result) {
    console.log("result.response.costtime:",             result.response.costtime);
    console.log("result.response.return: ",              result.response.return);
    console.log("result.response.arguments.sUserName:",  result.response.arguments.sUserName);
}

var error = function (result) {
    console.log("result.error: ",   result.response.error.code);
    console.log("result.message: ", result.response.error.message);
}

prx.yourName("kevintian").then(success, error).done();
