process.env.taf_stream   = "../../../../../taf-stream";
process.env.taf_rpc_path = "../../../../";

var TafSvr = require(process.env.taf_rpc_path).server;

var QueryFImpTaf = require("./QueryFImp.js").taf;

var svr = TafSvr.createServer();
svr.start({
    name         : "taf.tafregistry.QueryObjAdapter",
    servant      : "taf.tafregistry.QueryObj",
    endpoint     : "tcp -h 127.0.0.1 -p 17890 -t 10000",
    protocolName : "taf",
    maxconns     : 200000,
    handleImp    : QueryFImpTaf.QueryFImp
});

console.log("server started.");
