var Taf    = require("../../libjce.js");
require('should');

var os  = new Taf.JceOutputStream();

var buffer = new Taf.BinBuffer(Buffer.from("test write buffer"));
describe("test bytes", function() {
    it("#test for bytes encode and decode", function (){
        os.writeBytes(0,buffer);
        var is  = new Taf.JceInputStream(os.getBinBuffer());
        var decodeBuffer = is.readBytes(0,true,Taf.BinBuffer);
        decodeBuffer.toObject().toString().should.equal(buffer.toObject().toString());
    });
});
