var Promise=require("../").Promise;
var time=100000;
var interval=setInterval(function(){
    var promise=new Promise(function(resolve,reject){
        setTimeout(function(){
            resolve(666)
        },3000);
    });
    promise.then(function(data){
        console.log(data);
    });
    time--;
    if(time==0){
        clearInterval(interval);
    }
},1);

