'use strict';

var _authId = '63a5ff4002168b5cc9cd595d3b2e18cf';

exports.gen = function(dyeing, key, args) {
	var obj = {
		_authId : _authId,
		dyeing : !!dyeing
	};

	if (key) {
		obj.key = key;
	}

	if (args) {
		obj.args = args;
	}

	return obj;
};

exports.is = function(obj) {
	return obj && obj._authId === _authId && typeof obj.dyeing === 'boolean';
};