'use strict';

var config = require('winston/lib/winston/config');

var tafConfig = exports;

tafConfig.levels = {
  info : 4,
  debug : 3,
  warn : 2,
  error : 1,
  none : 0
};

tafConfig.colors = {
  info : 'white',
  debug : 'cyan',
  warn : 'yellow',
  error : 'red',
  none : 'grey'
};

config.taf = tafConfig;
config.addColors(config.taf.colors);