// 请求url：http://tafnodejstest.wsd.com/getServerInfo
// 模拟大量请求
var http=require("http");
var index=0;
function sendReq(){
    http.get('http://tafnodejstest.wsd.com/getServerInfo', (res) => {
        console.log(`213 normal rpc Got response: ${res.statusCode},  ${index++}`);
        // consume response body
        res.resume();
    }).on('error', (e) => {
        console.log(`Got error: ${e.message}`);
    });


    http.get('http://tafnodejstest.wsd.com/test_hash', (res) => {
        console.log(`213 test_hash  Got response: ${res.statusCode},  ${index++}`);
        // consume response body
        res.resume();
    }).on('error', (e) => {
        console.log(`Got error: ${e.message}`);
    });


    http.get('http://tafnodejstest.wsd.com/test_oneway', (res) => {
        console.log(`213 test_oneway Got response: ${res.statusCode},  ${index++}`);
        // consume response body
        res.resume();
    }).on('error', (e) => {
        console.log(`Got error: ${e.message}`);
    });
}

setInterval(sendReq,500)

