taf::Int32 NodeJsCommImp::getUsrName(const std::string & sUsrName, std::string &sValue1,std::string &sValue2,taf::JceCurrentPtr current) 
{
    LOG->debug() << "getUsrName:" << sUsrName << ",sValue1:" << sValue1 << ",sValue2:" << sValue2 << endl;

    sValue1 = sUsrName + "_sValue1";
    sValue2 = sUsrName + "_sValue2";
    
    return 1000;
}

taf::Int32 NodeJsCommImp::getall(const TRom::User_t & stUser, TRom::Result_t &stResult,taf::JceCurrentPtr current) 
{
    LOG->debug() << "getall:" << stUser.id << ", " << stUser.score << ", " << stUser.name << endl;
    
    stResult.id = stUser.id;
    stResult.iLevel = stUser.score;
    
    return 10001;
}

