#include "NodeJsTestServer.h"
#include "NodeJsCommImp.h"

using namespace std;

NodeJsTestServer g_app;

/////////////////////////////////////////////////////////////////
void
NodeJsTestServer::initialize()
{
	//initialize application here:
	//...

	addServant<NodeJsCommImp>(ServerConfig::Application + "." + ServerConfig::ServerName + ".NodeJsCommObj");
}
/////////////////////////////////////////////////////////////////
void
NodeJsTestServer::destroyApp()
{
	//destroy application here:
	//...
}
/////////////////////////////////////////////////////////////////
int
main(int argc, char* argv[])
{
	try
	{
		g_app.main(argc, argv);
		g_app.waitForShutdown();
	}
	catch (std::exception& e)
	{
		cerr << "std::exception:" << e.what() << std::endl;
	}
	catch (...)
	{
		cerr << "unknown exception." << std::endl;
	}
	return -1;
}
