var TafSvr      = require("@tencent/taf-rpc").server;
var Protocol    = require("@tencent/taf-rpc-json").server;
var Handle      = require("./EchoHandle.js").EchoHandle;

//http://git.oa.com/taf/taf-rpc/tree/master/

var option = new TafSvr.BindAdapterOption();
option.endpoint     = "tcp -h 127.0.0.1 -p 12306 -t 10000";
option.protocolName = "json";
option.protocol     = Protocol;
option.handleImp    = Handle;

var svr = TafSvr.createServer();
svr.start(option);
console.log("server started.");
