var Taf   = require("../../../../protal.js");
var TRom  = require("./NodeTafImp.js").TRom;

Taf.server.getServant("./TRom.NodeTafServer.config.conf").forEach(function (config){
    var svr, map;
    map = {
        'TRom.NodeTafServer.NodeTafObj' : TRom.NodeTafImp
    };

    svr = Taf.server.createServer(map[config.servant]);
    svr.start(config);
});
