const TafLog = require("@tencent/weplus-taf-logs")

const defaultConfig = {
  hasSufix: true,
  hasAppNamePrefix: true,
  formatter: TafLog.Formatter.Detail()
}
class WowLogger {
  constructor(name = "wow", config = defaultConfig) {
    this.logger = new TafLog("TafDate", name, config)
    this.name = name
    this.config = config
  }
}

WowLogger.getLogger = name => new TafLog("TafDate", name, defaultConfig)

module.exports = WowLogger
